question.answer:
#include <stdio.h>

int main()
{
  float gesamtpreis = 0;
  unsigned int entfernung = 0;
  int rabatt_prozent = 0;
  unsigned int auswahl = 0;
  printf("Ahoi bei Yum Yum Pizza!\n drücken Sie ...\n");
  while(auswahl != 3)
  {
    printf(" '1' fuer Salami Pizza dazu \n '2' fuer Veggi Pizza dazu \n '3' fuer Bestellung abschliessen \n");
    scanf( "%i", &auswahl );
    switch (auswahl)
    {
      case 1: 
        printf("Einmal Pizza Salami dazu, fuer 5.50\n");
        gesamtpreis += 5.50;
        break;
      case 2: 
        printf("Einmal Pizza Veggi dazu, fuer 4.30\n");
        gesamtpreis += 4.30;
        break;
    }
  }
  printf("\ndas wars? ...noch eine Frage: \nWelche Lieferentfernung hat die Bestellung (in km)? ");
  scanf( "%i", &entfernung );
  if(entfernung <= 2) rabatt_prozent += 10;
  if(entfernung >= 20) rabatt_prozent += 10;
  if (gesamtpreis > 10) rabatt_prozent += 10;
  if (gesamtpreis > 20) rabatt_prozent += 10;
  gesamtpreis -= gesamtpreis * (rabatt_prozent/100.0);
  printf("\nDas sind mit %i %% Rabatt genau %.2f Euro.\n", rabatt_prozent , gesamtpreis);
}
