#include <stdio.h>

int main()
{
    unsigned int jahreszahl;

    printf("Das Programm kann Schaltjahre bestimmen - welches Jahr soll getestet werden? ");
    scanf("%i", &jahreszahl);

    if (jahreszahl % 400 == 0) printf("\n%i ist ein Schaltjahr.\n", jahreszahl); //durch 400 ohne Rest teilbar?
    else if ((jahreszahl % 100 != 0) && (jahreszahl % 4 == 0))
        printf("\n%i ist ein Schaltjahr.\n", jahreszahl); //alle 4
    else printf("\n%i ist kein Schaltjahr.\n", jahreszahl); // alles andere
}
