question.answer:
#include <stdio.h>

void menue()
{
    printf("Bitte auswaehlen...\n"
           "\tPPR ist [t]oll\n"
           "\tPPR [s]tinkt mir\n"
           "\tPPR, Flasche [l]eer.\n\n");
    char eingabe = getchar();
    getchar(); // "enter" aus dem Tastaturpuffer entfernen
    switch (eingabe)
    {
        case 't':
            printf("Wir moegen dich auch.\n");
            break;
        case 's':
            printf("Hauptsache wir stinken geruchstechnisch nicht.\n");
            break;
        case 'l':
            printf("Giovanni, lernst du Programmieren?\n");
            break;
        default:
            printf("Das stand gar nicht zur Auswahl. Auf wiedersehen.\n");
            eingabe = 0;
    }
}
