question.answer:
#include <stdio.h>

int main()
{
   int gesamtsumme = 100;
   int eingabe = 0;
   unsigned char spieler1Dran = 1;
 
   printf("Kuerzel von Spieler 1 eingeben (ein Buchstabe): ");
   char spieler1Kuerzel = getchar();
   getchar(); // Enter aus dem Tastaturpuffer entfernen
   printf("Kuerzel von Spieler 2 eingeben (ein Buchstabe): ");
   char spieler2Kuerzel = getchar();
   getchar(); // Enter aus dem Tastaturpuffer entfernen

   // Spiel laeuft so lange, bis gesamtsumme <= 0 ist.
   while (gesamtsumme > 0)
   {
      // Personalisierte Eingabeaufforderung fuer Zahl
      if(spieler1Dran){
         printf("%c", spieler1Kuerzel);
      }else{
         printf("%c", spieler2Kuerzel);
      }
      printf(", bitte eine Zahl zwischen 1 und 10 eingeben: ");
      scanf("%i", &eingabe);
   
      // Solange die Eingabe inkorrekt ist auffordern neue Zahl einzugeben
      while(eingabe < 1 || eingabe > 10){
         printf("Fehlerhafte Zahl. Bitte erneut eine Zahl eingeben: ");
         scanf("%i", &eingabe);
      }
   
      // Eingabe von der Gesamtsumme abziehen
      gesamtsumme = gesamtsumme - eingabe;
      printf("Aktuelle Summe: %i\n", gesamtsumme);
   
      // Spielerwechsel
      spieler1Dran = !spieler1Dran;
   }
   // Gesamtsumme ist nun kleiner oder gleich 0.
   
   if(spieler1Dran){
         printf("Herzlichen Glückwunsch %c. Du hast gewonnen!\n", spieler2Kuerzel);
   }else{
         printf("Herzlichen Glückwunsch %c. Du hast gewonnen!\n", spieler1Kuerzel);
   }
}
